mapper.insert:
   SELECT REZ_NO_SEQ.NEXTVAL FROM DUAL


INSERT INTO WD_REZ (
    REZ_NO,
    USER_ID
) VALUES (
    #{rezNo},
    #{userId}
)

mapper.update:
UPDATE WD_REZ
SET
    WH_CODE = #{whCode},
    H_CODE = #{hCode}
WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET
    S_CODE = #{sCode},
    S_DATE = #{sDate}
WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET
    DC_CODE = #{dcCode},
    D_NO = #{dNo}
WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET
    M_CODE = #{mCode},
    M_PLUS_NUM = #{mPlusNum}
WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET
    REZ_DATE = SYSDATE,
    WDATE = #{wDate}
WHERE
    REZ_NO = #{rezNo}
AND
    USER_ID = #{userId}

UPDATE WD_REZ
SET M_CODE = '', M_PLUS_NUM = ''
WHERE REZ_NO = #{rezNo} AND USER_ID = #{userId}
